styles:
body {
  font-family: Arial, sans-serif;
  background: #f4f4f4;
  margin: 0;
  padding: 0;
}

.resume {
  display: grid;
  grid-template-columns: 1fr 2fr;
  max-width: 900px;
  margin: 30px auto;
  background: #fff;
  box-shadow: 0 2px 6px rgba(0,0,0,0.15);
}

.resume .left {
  background: #2f3e46;
  color: #fff;
  padding: 30px 20px;
}

.resume .left img {
  width: 120px;
  height: 120px;
  object-fit: cover;
  border-radius: 50%;
  display: block;
  margin: 0 auto 20px;
  border: 3px solid #fff;
}

.resume .left h2 {
  margin-top: 25px;
  font-size: 18px;
  border-bottom: 1px solid rgba(255,255,255,0.3);
  padding-bottom: 6px;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.resume .left p,
.resume .left li {
  font-size: 14px;
  line-height: 1.5;
}

.resume .left ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.resume .right {
  padding: 30px 40px;
}

.resume .right h1 {
  font-size: 28px;
  margin: 0 0 15px;
  color: #2f3e46;
}

.resume .right h2 {
  margin-top: 20px;
  font-size: 20px;
  color: #354f52;
  border-bottom: 2px solid #84a98c;
  padding-bottom: 6px;
}

.resume .right p {
  font-size: 15px;
  line-height: 1.6;
  margin: 10px 0;
  color: #222;
}

.resume .job {
  margin-bottom: 20px;
}

.resume .job h3 {
  font-size: 16px;
  margin: 0 0 4px;
  color: #2f3e46;
}

.resume .job p em {
  font-style: italic;
  font-size: 13px;
  color: #666;
}
