styles:
body { font-family: Arial, sans-serif; background: #111; color: #fff; }
.resume { display: grid; grid-template-columns: 1fr 2fr; gap: 20px; }
.left, .right { background: #222; padding: 20px; border-radius: 8px; }
img { width: 100%; border-radius: 8px; margin-bottom: 15px; }
h1 { font-size: 3rem; margin: 0 0 20px; }
h2 { font-size: 1.2rem; margin: 20px 0 10px; }
h3 { margin: 10px 0 5px; font-size: 1rem; }
p { margin: 5px 0; line-height: 1.5; }
ul { padding-left: 20px; margin: 0; }
li { margin-bottom: 5px; }
