styles:
/* Template CSS â€” kept as a single JSON string with \n for newlines */
body {
  font-family: Inter, system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  background: #f2f2f4;
  margin: 0;
  padding: 24px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

.resume {
  max-width: 920px;
  margin: 36px auto;
  display: grid;
  grid-template-columns: 320px 1fr;
  gap: 0;
  background: #ffffff;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(16,24,40,0.08);
}

/* LEFT SIDEBAR (dark) */
.resume .left {
  background: #1f1b1f; /* deep charcoal */
  color: #f3efe9;
  padding: 32px 26px;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.left__photo {
  width: 140px;
  height: 140px;
  border-radius: 50%;
  object-fit: cover;
  border: 4px solid rgba(255,255,255,0.12);
  box-shadow: 0 6px 18px rgba(0,0,0,0.4);
}

.left__name {
  margin-top: 18px;
  font-size: 18px;
  font-weight: 700;
  letter-spacing: 0.6px;
  text-align: center;
}

.left__subtitle {
  margin-top: 6px;
  font-size: 13px;
  color: rgba(255,255,255,0.7);
}

.left__sectionTitle {
  width: 100%;
  margin-top: 26px;
  font-size: 12px;
  text-transform: uppercase;
  letter-spacing: 1.6px;
  color: rgba(255,255,255,0.85);
  border-bottom: 1px solid rgba(255,255,255,0.04);
  padding-bottom: 8px;
}

.left__list {
  margin: 12px 0 0 0;
  padding: 0;
  width: 100%;
  list-style: none;
}

.left__list li {
  font-size: 13px;
  color: rgba(255,255,255,0.9);
  margin: 8px 0;
  line-height: 1.45;
}

.left__skill {
  display: inline-block;
  background: rgba(255,255,255,0.06);
  color: rgba(255,255,255,0.95);
  padding: 6px 10px;
  border-radius: 999px;
  font-size: 12px;
  margin: 6px 6px 0 0;
}

/* RIGHT CONTENT (white) */
.resume .right {
  padding: 36px 44px;
  background: #ffffff;
}

.right__header {
  display: flex;
  align-items: baseline;
  justify-content: space-between;
  gap: 12px;
}

.right h1 {
  margin: 0 0 6px 0;
  font-size: 34px;
  color: #111827;
  letter-spacing: 0.4px;
}

.right__titleSmall {
  color: #6b7280;
  font-size: 13px;
}

.sectionTitle {
  margin-top: 26px;
  font-size: 16px;
  color: #111827;
  border-bottom: 2px solid #efecec;
  padding-bottom: 8px;
}

.profile p {
  margin: 12px 0 0 0;
  color: #374151;
  font-size: 15px;
  line-height: 1.6;
}

.job {
  margin-top: 14px;
  padding-top: 10px;
  border-left: 3px solid transparent;
}

.job h3 {
  margin: 0;
  font-size: 16px;
  color: #0f172a;
}

.job .meta {
  font-size: 13px;
  color: #6b7280;
  margin: 4px 0 8px 0;
}

.job p { color: #374151; font-size: 14px; margin: 0 0 8px 0; }

/* responsive */
@media (max-width: 820px) {
  .resume { grid-template-columns: 1fr; margin: 18px; }
  .left { flex-direction: row; gap: 16px; align-items: center; padding: 20px; }
  .left__photo { width: 88px; height: 88px; }
  .right { padding: 20px; }
}
